package com.aistudio.advertisementyandexdemoapp.navigationimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.navigation.NavHostControllerimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport com.aistudio.advertisementyandexdemoapp.screens.BannerScreenimport com.aistudio.advertisementyandexdemoapp.screens.FullScreenAdvertisementimport com.aistudio.advertisementyandexdemoapp.screens.NativeAdvertisementScreenimport com.aistudio.advertisementyandexdemoapp.screens.VideoAdvertisementScreen@Composablefun AppNavigation(    modifier: Modifier = Modifier,    navController: NavHostController,) {    NavHost(navController = navController, startDestination = BottomNavigationItems.Banners.route) {        composable(BottomNavigationItems.Banners.route) { BannerScreen(modifier = modifier) }        composable(BottomNavigationItems.FullScreen.route) { FullScreenAdvertisement(modifier = modifier) }        composable(BottomNavigationItems.Video.route) { VideoAdvertisementScreen(modifier = modifier) }        composable(BottomNavigationItems.Native.route) { NativeAdvertisementScreen(modifier = modifier) }    }}