package com.aistudio.advertisementyandexdemoapp.screensimport androidx.compose.foundation.ExperimentalFoundationApiimport androidx.compose.foundation.layout.*import androidx.compose.foundation.lazy.LazyColumnimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.platform.LocalContextimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport androidx.compose.ui.viewinterop.AndroidViewimport com.yandex.mobile.ads.banner.AdSizeimport com.yandex.mobile.ads.banner.BannerAdViewimport com.yandex.mobile.ads.common.AdRequest@OptIn(ExperimentalFoundationApi::class)@Composablefun BannerScreen(modifier: Modifier) {    val bannerAdView = BannerAdView(LocalContext.current)    val bannerAdViewSecond = BannerAdView(LocalContext.current)    val bannerAdViewThird = BannerAdView(LocalContext.current)    val bannerAdViewFour = BannerAdView(LocalContext.current)    Box(modifier = modifier.fillMaxSize(), contentAlignment = Alignment.Center) {        Text(text = "List of banner ads",            fontSize = 24.sp,            fontWeight = FontWeight(700),            color = Color.Black,            modifier = modifier                .padding(top = 25.dp)                .align(Alignment.TopCenter))        LazyColumn(modifier = modifier.padding(top = 60.dp, bottom = 25.dp ),verticalArrangement = Arrangement.spacedBy(8.dp)) {            item {                AndroidView(modifier = modifier.wrapContentSize(), factory = {                    bannerAdView.apply {                        bannerAdView.setAdUnitId("R-M-2130972-1")                        bannerAdView.setAdSize(AdSize.flexibleSize(400,150))                        val request = AdRequest.Builder().build()                        bannerAdView.loadAd(request)                    }                })            }            item {                AndroidView(modifier = modifier.wrapContentSize(), factory = {                    bannerAdViewSecond.apply {                        bannerAdViewSecond.setAdUnitId("R-M-2130972-5")                        bannerAdViewSecond.setAdSize(AdSize.flexibleSize(300, 200))                        val requestSecond = AdRequest.Builder().build()                        bannerAdViewSecond.loadAd(requestSecond)                    }                })            }            item {                AndroidView(modifier = modifier.wrapContentSize(), factory = {                    bannerAdViewThird.apply {                        bannerAdViewThird.setAdUnitId("R-M-2130972-6")                        bannerAdViewThird.setAdSize(AdSize.stickySize(400))                        val requestThird = AdRequest.Builder().build()                        bannerAdViewThird.loadAd(requestThird)                    }                })            }            item {                AndroidView(modifier = modifier.wrapContentSize(), factory = {                    bannerAdViewFour.apply {                        bannerAdViewFour.setAdUnitId("R-M-2130972-7")                        bannerAdViewFour.setAdSize(AdSize.stickySize(400))                        val requestFour = AdRequest.Builder().build()                        bannerAdViewFour.loadAd(requestFour)                    }                })            }        }    }}