package com.aistudio.advertisementyandexdemoapp.screensimport android.content.Contextimport android.util.Logimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.platform.LocalContextimport androidx.compose.ui.viewinterop.AndroidViewimport com.yandex.mobile.ads.common.AdRequestimport com.yandex.mobile.ads.common.AdRequestErrorimport com.yandex.mobile.ads.common.ImpressionDataimport com.yandex.mobile.ads.instream.player.ad.InstreamAdViewimport com.yandex.mobile.ads.interstitial.InterstitialAdimport com.yandex.mobile.ads.interstitial.InterstitialAdEventListener@Composablefun FullScreenAdvertisement(modifier: Modifier) {    val bannerFullScreen = InterstitialAd(LocalContext.current)    Box(modifier = modifier.fillMaxSize(), contentAlignment = Alignment.Center) {        AndroidView(            factory = { context: Context ->                InstreamAdView(context).apply {                    bannerFullScreen.setAdUnitId("R-M-2130972-3")                    val request = AdRequest.Builder().build()                    bannerFullScreen.loadAd(request)                    bannerFullScreen.setInterstitialAdEventListener(object :                        InterstitialAdEventListener {                        override fun onAdLoaded() { bannerFullScreen.show() }                        override fun onAdFailedToLoad(p0: AdRequestError) {                            Log.i("Full_screen", p0.description)                        }                        override fun onAdShown() {}                        override fun onAdDismissed() {}                        override fun onAdClicked() {}                        override fun onLeftApplication() {}                        override fun onReturnedToApplication() {}                        override fun onImpression(p0: ImpressionData?) {}                    })                }            }        )    }}