package com.aistudio.advertisementyandexdemoapp.screensimport android.content.Contextimport android.util.Logimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.lazy.LazyColumnimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.platform.LocalContextimport androidx.compose.ui.unit.dpimport androidx.compose.ui.viewinterop.AndroidViewimport com.yandex.mobile.ads.common.AdRequestErrorimport com.yandex.mobile.ads.nativeads.NativeAdimport com.yandex.mobile.ads.nativeads.NativeAdLoadListenerimport com.yandex.mobile.ads.nativeads.NativeAdLoaderimport com.yandex.mobile.ads.nativeads.NativeAdRequestConfigurationimport com.yandex.mobile.ads.nativeads.template.NativeBannerView@Composablefun NativeAdvertisementScreen(modifier: Modifier) {    val native = NativeAdLoader(LocalContext.current)    val nativeSecond = NativeAdLoader(LocalContext.current)    val adFoxParameters = mapOf(        "adf_ownerid" to "270901",        "adf_p1" to "cqtgi",        "adf_p2" to "fksh"    )    Box(modifier = modifier        .padding(horizontal = 8.dp)        .fillMaxSize(), contentAlignment = Alignment.Center) {        LazyColumn(modifier = modifier            .padding(top = 25.dp),            verticalArrangement = Arrangement.spacedBy(6.dp)) {            item {                AndroidView(                    factory = { context: Context ->                        val request = NativeAdRequestConfiguration                            .Builder("R-M-2130972-2")                            .setParameters(adFoxParameters)                            .setShouldLoadImagesAutomatically(true)                            .build()                        native.loadAd(request)                        NativeBannerView(context).apply {                            native.setNativeAdLoadListener(object : NativeAdLoadListener {                                override fun onAdLoaded(nativeAd: NativeAd) {                                    Log.i("Native Load", "Native ad loaded")                                    setAd(nativeAd)                                }                                override fun onAdFailedToLoad(error: AdRequestError) {                                    Log.i("Native_Error",                                        "Native ad failed to load with code ${error.code}: ${error.description}")                                }                            })                        }                    }                )            }            item {                AndroidView(                    factory = { context: Context ->                        val requestSecond = NativeAdRequestConfiguration                            .Builder("R-M-2130972-8")                            .setParameters(adFoxParameters)                            .setShouldLoadImagesAutomatically(true)                            .build()                        nativeSecond.loadAd(requestSecond)                        NativeBannerView(context).apply {                            nativeSecond.setNativeAdLoadListener(object : NativeAdLoadListener {                                override fun onAdLoaded(nativeAd: NativeAd) {                                    Log.i("Native LoadSecond", "NativeSecond ad loaded")                                    setAd(nativeAd)                                }                                override fun onAdFailedToLoad(error: AdRequestError) {                                    Log.i("NativeSecond Error",                                        "Native ad failed to load with code ${error.code}: ${error.description}")                                }                            })                        }                    }                )            }        }    }}