package com.aistudio.advertisementyandexdemoapp.screensimport androidx.compose.foundation.borderimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.shape.RoundedCornerShapeimport androidx.compose.material.Scaffoldimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.unit.dpimport androidx.navigation.compose.rememberNavControllerimport com.aistudio.advertisementyandexdemoapp.navigation.AppNavigationimport com.aistudio.advertisementyandexdemoapp.navigation.BottomNavigationBar@Composablefun TitleScreen(modifier: Modifier) {    val navController = rememberNavController()    Scaffold(        topBar = { },        bottomBar = { BottomNavigationBar(navController = navController, modifier = modifier) },        content = { padding ->            Box(modifier = Modifier                .padding(padding)                .border(width = 1.dp, color = Color.Black, shape = RoundedCornerShape(20.dp))            ) {                AppNavigation(navController = navController)            }        },        backgroundColor = Color.Yellow    )}