package com.aistudio.advertisementyandexdemoapp.screensimport android.content.Contextimport android.util.Logimport android.widget.Toastimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.runtime.mutableStateOfimport androidx.compose.runtime.rememberimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.platform.LocalContextimport androidx.compose.ui.unit.spimport androidx.compose.ui.viewinterop.AndroidViewimport com.yandex.mobile.ads.common.AdRequestimport com.yandex.mobile.ads.common.AdRequestErrorimport com.yandex.mobile.ads.common.ImpressionDataimport com.yandex.mobile.ads.instream.player.ad.InstreamAdViewimport com.yandex.mobile.ads.rewarded.Rewardimport com.yandex.mobile.ads.rewarded.RewardedAdimport com.yandex.mobile.ads.rewarded.RewardedAdEventListener@Composablefun VideoAdvertisementScreen(modifier: Modifier) {    val context = LocalContext.current    val rewardedAd = RewardedAd(context)    val rewardValue = remember { mutableStateOf("") }    Box(modifier = modifier.fillMaxSize(), contentAlignment = Alignment.Center) {        Text(text = rewardValue.value, modifier = modifier, fontSize = 18.sp)        AndroidView(factory = { context: Context ->            InstreamAdView(context).apply {                rewardedAd.setAdUnitId("R-M-2130972-4")                val request = AdRequest.Builder().build()                rewardedAd.loadAd(request)                rewardedAd.setRewardedAdEventListener(object : RewardedAdEventListener {                    override fun onAdLoaded() {                        rewardedAd.show()                    }                    override fun onAdFailedToLoad(p0: AdRequestError) {                        Log.i("RewardedAd", p0.description)                    }                    override fun onAdShown() {}                    override fun onAdDismissed() {}                    override fun onRewarded(reward: Reward) {                        Log.i("TTT", "Reward: ${reward.amount} of ${reward.type}")                        rewardValue.value = "Reward: ${reward.amount} of ${reward.type}"                    }                    override fun onAdClicked() {}                    override fun onLeftApplication() {}                    override fun onReturnedToApplication() {                        Toast.makeText(context, "Вы получили награду", Toast.LENGTH_SHORT).show()                    }                    override fun onImpression(p0: ImpressionData?) {}                })            }        })    }}